member.example:
using (TaggedScopeRuntime.UseVersion(TaggedScopesVersion.V3))
{
    // All CreateScope calls will use V3
}

// Window can satisfy Region (parent → child)
ScopeTagV2.Window().CanSatisfy(ScopeTagV2.Region()) == true

// Transaction can satisfy Database
ScopeTagV2.Transaction().CanSatisfy(ScopeTagV2.Database()) == true

// Region cannot satisfy Window (child → parent is NOT allowed)
ScopeTagV2.Region().CanSatisfy(ScopeTagV2.Window()) == false
